	('bc0e8028-48e0-4ccc-aa1a-d6ecb2edd0c2', 'csrd', 'Corporate Sustainability Reporting Directive', '2024', 'The EU Corporate Sustainability Reporting Directive (CSRD) requires companies to report on their sustainability performance using European Sustainability Reporting Standards (ESRS). It covers environmental, social, and governance topics.', 'European Commission', 'https://finance.ec.europa.eu/capital-markets-union-and-financial-markets/company-reporting-and-auditing/company-reporting/corporate-sustainability-reporting_en', true, NULL, true, NULL, '2026-01-21 18:30:20.045759+00', '2026-01-21 18:30:20.045759+00', 'Corporate Sustainability Reporting Directive', 'csrd', '2024', 'Regulatory Compliance', 2, 0.00),
	('f283cd4b-b5d8-42a1-8a48-53167757211f', 'sbti', 'Science Based Targets initiative', '2.0', 'Science Based Targets initiative (SBTi) enables companies to set greenhouse gas emissions reduction targets that are consistent with what climate science says is needed to meet the goals of the Paris Agreement.', 'SBTi', 'https://sciencebasedtargets.org/', true, NULL, true, NULL, '2026-01-21 18:30:20.045759+00', '2026-01-21 18:30:20.045759+00', 'Science Based Targets initiative', 'sbti', '2.0', 'Climate Targets', 3, 0.00),
	('420354c6-25a1-4042-8e3a-ce3fddff9461', 'gri', 'Global Reporting Initiative Standards', '2021', 'The Global Reporting Initiative (GRI) Standards help organizations understand and communicate their impacts on the economy, environment, and people. GRI reporting is the world''s most widely used sustainability reporting framework.', 'GRI', 'https://www.globalreporting.org/', true, NULL, true, NULL, '2026-01-21 18:30:20.045759+00', '2026-01-21 18:30:20.045759+00', 'Global Reporting Initiative Standards', 'gri', '2021', 'Reporting Standards', 4, 0.00),
	('98d8efe8-9956-4461-967c-af2b2b838300', 'iso14001', 'ISO 14001', '2015', 'ISO 14001 is an internationally agreed standard that sets out the requirements for an environmental management system. It helps organizations improve their environmental performance through more efficient use of resources and reduction of waste.', 'International Organization for Standardization', 'https://www.iso.org/iso-14001-environmental-management.html', true, NULL, true, NULL, '2026-01-21 18:30:59.910393+00', '2026-01-21 18:30:59.910393+00', 'ISO 14001 Environmental Management', 'iso14001', '2015', 'Environmental Management', 5, 0.00),
	('53ac5afb-a3b7-4956-a07d-42079798dae7', 'iso50001', 'ISO 50001', '2018', 'ISO 50001 provides a framework for establishing, implementing, maintaining and improving an energy management system. It enables organizations to follow a systematic approach in achieving continual improvement of energy performance.', 'International Organization for Standardization', 'https://www.iso.org/iso-50001-energy-management.html', true, NULL, true, NULL, '2026-01-21 18:30:59.910393+00', '2026-01-21 18:30:59.910393+00', 'ISO 50001 Energy Management', 'iso50001', '2018', 'Energy Management', 6, 0.00),
	('8004e37d-1472-48a6-bb18-9462daee0fbc', 'cdp_climate', 'CDP Climate Change', '2024', 'CDP Climate Change questionnaire enables companies to measure and manage their environmental impacts by disclosing information about climate change risks, opportunities, and emissions.', 'CDP', 'https://www.cdp.net/', true, NULL, true, NULL, '2026-01-21 18:30:59.910393+00', '2026-01-21 18:30:59.910393+00', 'CDP Climate Change Disclosure', 'cdp_climate', '2024', 'Climate Disclosure', 7, 0.00),
	('ccc5d243-19c5-4205-aeb6-bd495ce444d9', 'ecovadis', 'EcoVadis', '2024', 'EcoVadis provides business sustainability ratings, assessing companies on their environmental, social, and ethical performance based on international CSR standards.', 'EcoVadis', 'https://ecovadis.com/', true, NULL, true, 45.00, '2026-01-21 18:30:59.910393+00', '2026-01-21 18:30:59.910393+00', 'EcoVadis Sustainability Rating', 'ecovadis', '2024', 'Supply Chain Rating', 8, 0.00);
